import { retryWithExponentialBackoff } from "./retryWithExponentialBackoff.js";
import { throttleUnlimitedConcurrency } from "./throttleUnlimitedConcurrency.js";
export const callWithRetryAndThrottle = async ({ retry = retryWithExponentialBackoff(), throttle = throttleUnlimitedConcurrency(), call, }) => retry(async () => throttle(call));
