export class NoSuchToolError extends Error {
    constructor(toolName) {
        super(`No such tool: ${toolName}`);
        Object.defineProperty(this, "toolName", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: void 0
        });
        this.name = "NoSuchToolError";
        this.toolName = toolName;
    }
}
